/**
 * This component shows a list of Article
 * @class ArticleListComponent
 */
@Component({
    selector: 'app-article-list',
    templateUrl: './article-list.component.html',
    styleUrls: ['./article-list.component.css']
})
export class ArticleListComponent implements OnInit {
    list: Article[];
    search: any = {};
    idSelected: string;
    constructor(
        private articleService: ArticleService,
    ) { }

    /**
     * Init
     */
    ngOnInit(): void {
        this.articleService.list().subscribe(list => this.list = list);
    }

    /**
     * Select Article to remove
     *
     * @param {string} id Id of the Article to remove
     */
    selectId(id: string) {
        this.idSelected = id;
    }

    /**
     * Remove selected Article
     */
    deleteItem() {
        this.articleService.remove(this.idSelected).subscribe(data => this.list = this.list.filter(el => el._id !== this.idSelected));
    }

}
